select  id,device_no, gun_mac from device_gun


        state= #{state,jdbcType=INTEGER}


        and device_no like '%' #{deviceNo} '%'

SELECT dg.id,dg.device_no, (SELECT g.gun_tag FROM gun AS g WHERE g.bluetooth_mac= dg.gun_mac) AS gun_mac, (SELECT d.state FROM device AS d WHERE d.device_no= dg.device_no) AS deviceState, (SELECT g.state FROM gun AS g WHERE g.bluetooth_mac= dg.gun_mac) AS gunState, dg.create_time FROM device_gun dg
dg.state= #{state,jdbcType=INTEGER}
and dg.device_no like '%' #{deviceNo} '%'
