mapper.select:
select * from mission


    and id=#{id}


    and device_no=#{deviceNo}


    and web_user_id=#{webUserId}


    and type=#{type}


    and send_time ='%' #{sendTime} '%'


    and state like '%' #{state} '%'


    and read_state like '%' #{readState} '%'


    and body like '%' #{body} '%'


    and create_time like '%' #{createTime} '%'


    and update_time like '%' #{updateTime} '%'


    and version=#{version}
